rule "mailcow_netfilter_details"
when
  has_field("netfilter_ip")
then
  let geo = lookup("geo-ip-city-maxmind-lookup", to_string($message.netfilter_ip));
  set_field("netfilter_ip_geolocation", geo["coordinates"]);
  set_field("netfilter_ip_geo_country_code", geo["country"].iso_code);
  set_field("netfilter_ip_geo_country_name", geo["country"].names.en);

  let whoice = lookup("ip-whoice", to_ip($message.netfilter_ip));
  set_field("netfilter_ip_organisation", whoice.single_value);

end

rule "mailcow_postfix_ip_details"
when
  has_field("postfix_client_ip")
then
  let geo = lookup("geo-ip-city-maxmind-lookup", to_string($message.postfix_client_ip));
  set_field("postfix_client_ip_geolocation", geo["coordinates"]);
  set_field("postfix_client_ip_geo_country_code", geo["country"].iso_code);
  set_field("postfix_client_ip_geo_country_name", geo["country"].names.en);

  let whoice = lookup("ip-whoice", to_ip($message.postfix_client_ip));
  set_field("postfix_client_ip_organisation", whoice.single_value);

end

rule "mailcow_postfix"
when
  has_field("container_name") AND contains("mailcowdockerized_postfix-mailcow_1",to_string($message.container_name))
then
  let original_message = to_string($message.message);
  let parsed_field_smtp = grok(pattern: "%{POSTFIX_SMTP}", value: original_message, only_named_captures: true);
  let parsed_field_smtpd = grok(pattern: "%{POSTFIX_SMTPD}", value: original_message, only_named_captures: true);
  set_fields(parsed_field_smtp);
  set_fields(parsed_field_smtpd);
end

rule "mailcow_netfilter_grok"
when
//  has_field("container_name") AND contains("mailcowdockerized_netfilter-mailcow_1",to_string($message.container_name))
  true
then
  let original_message = to_string($message.message);
  let parsed_field_matched_rule = grok(pattern: "%{IP:netfilter_ip} matched rule id %{NUMBER:netfilter_rule_id}", value: original_message, only_named_captures: true);
  let parsed_field_banned = grok(pattern: "Banning %{IP:netfilter_ip}/%{NUMBER} for %{NUMBER:netfilter_ban_time} minutes", value: original_message, only_named_captures: true);
  set_fields(parsed_field_matched_rule);
  set_fields(parsed_field_banned);
end
